const rows = [
  techStack.slice(0, 7),
  techStack.slice(7, 14),
  techStack.slice(14, 20),
  // techStack.slice(12, 16),
]

const TechCard = ({ name, icon }: { name: string; icon: any }) => {
  return (
    <div
      className={cn(
        "flex items-center gap-3 rounded-xl px-4 py-2 border",
        "bg-white dark:bg-black",
        "border-gray-200 dark:border-white/10"
      )}
    >
      {icon}
      <span className="text-sm font-medium text-gray-900 dark:text-white">
        {name}
      </span>
    </div>
  )
}

export default function MarqueeTechStack() {
  return (
    <div className="relative flex w-full flex-col items-center justify-center overflow-hidden gap-3 pt-2">
      {rows.map((row, i) => (
        <Marquee
          key={i}
          pauseOnHover
          reverse={i % 2 !== 0}
          className="[--duration:25s]"
        >
          {row.map((tech) => (
            <TechCard key={tech.name} {...tech} />
          ))}
        </Marquee>
      ))}
      <div className="pointer-events-none absolute inset-y-0 left-0 w-1/4 bg-gradient-to-r from-background"></div>
      <div className="pointer-events-none absolute inset-y-0 right-0 w-1/4 bg-gradient-to-l from-background"></div>
    </div>
  )
}
